       return null;
   } else if(entity != null) {
       return entity;
   } else {
       entity = this.loadFromSecondLevelCache(event, persister, keyToLoad);
       if(entity != null) {
          //  ~
       } else {
           entity = this.loadFromDatasource(event, persister);
       }
       return entity;
   }
}
